import React, { useState } from 'react';
import { Select, InputNumber, Button, Card } from 'antd';
import { SearchOutlined, SwapOutlined } from '@ant-design/icons';
import { airports } from '../../data/airports';
import airlines from '../../data/airlines';

const SearchForm = ({ onSearch, isLoading, errors }) => {
  const [departure, setDeparture] = useState(null);
  const [arrival, setArrival] = useState(null);
  const [selectedAirlines, setSelectedAirlines] = useState([]);
  const [maxSegments, setMaxSegments] = useState(4);

  // Sort airlines alphabetically
  const sortedAirlines = [...airlines]
    .map(airline => ({
      ...airline,
      searchStr: `${airline.value} ${airline.label}`.toLowerCase()
    }))
    .sort((a, b) => a.label.localeCompare(b.label));

  const airportSelectProps = {
    showSearch: true,
    allowClear: true,
    suffixIcon: null,
    options: airports.map(airport => ({
      value: airport.IATA,
      label: `${airport.IATA} - ${airport.Name} (${airport.Country})`,
      searchStr: `${airport.IATA} ${airport.Name}`.toLowerCase()
    })),
    filterOption: (input, option) => {
      if (!input) return true;
      input = input.toString().toLowerCase();

      // Check for IATA matches - IATA is in option.value
      const iataCode = option.value.toLowerCase();
      const isExactIataMatch = iataCode === input;
      const isIataStartsWith = iataCode.startsWith(input);
      const isIataContains = iataCode.includes(input);

      // Check for name matches
      const airportName = option.label.toLowerCase();
      const isNameStartsWith = airportName.startsWith(input);
      const isNameContains = airportName.includes(input);

      // Set priority for sorting
      if (isExactIataMatch) {
        option.priority = 100; // Highest priority
      } else if (isIataStartsWith) {
        option.priority = 80;
      } else if (isIataContains) {
        option.priority = 60;
      } else if (isNameStartsWith) {
        option.priority = 40;
      } else if (isNameContains) {
        option.priority = 20;
      } else {
        option.priority = 0;
      }

      return isExactIataMatch || isIataStartsWith || isIataContains || isNameStartsWith || isNameContains;
    },
    filterSort: (optionA, optionB) => {
      const priorityDiff = optionB.priority - optionA.priority;
      if (priorityDiff !== 0) {
        return priorityDiff; // Higher priority comes first
      }

      // If same priority, sort alphabetically by IATA code
      return optionA.value.localeCompare(optionB.value);
    },
    listHeight: 256,
    virtual: true,
    dropdownStyle: { maxHeight: 400 }
  };

  const airlineSelectProps = {
    showSearch: true,
    allowClear: true,
    filterOption: (input, option) => {
      if (!input) return true;
      const searchInput = input.toString().toLowerCase();

      // Check for airline code matches
      const airlineCode = option.value.toLowerCase();
      const isExactCodeMatch = airlineCode === searchInput;
      const isCodeStartsWith = airlineCode.startsWith(searchInput);
      const isCodeContains = airlineCode.includes(searchInput);

      // Check for airline name matches
      const airlineName = option.label.toLowerCase();
      const isNameStartsWith = airlineName.startsWith(searchInput);
      const isNameContains = airlineName.includes(searchInput);

      // Set priority for sorting
      if (isExactCodeMatch) {
        option.priority = 100; // Highest priority
      } else if (isCodeStartsWith) {
        option.priority = 80;
      } else if (isCodeContains) {
        option.priority = 60;
      } else if (isNameStartsWith) {
        option.priority = 40;
      } else if (isNameContains) {
        option.priority = 20;
      } else {
        option.priority = 0;
      }

      return isExactCodeMatch || isCodeStartsWith || isCodeContains || isNameStartsWith || isNameContains;
    },
    filterSort: (optionA, optionB) => {
      const priorityDiff = optionB.priority - optionA.priority;
      if (priorityDiff !== 0) {
        return priorityDiff; // Higher priority comes first
      }

      // If same priority, sort alphabetically by airline code
      return optionA.value.localeCompare(optionB.value);
    },
    listHeight: 400,
    virtual: false,
    menuItemSelectedIcon: null,
    dropdownStyle: { maxHeight: 400 }
  };

  const handleSubmit = () => {
    onSearch({
      departure,
      arrival,
      selectedAirlines,
      maxSegments
    });
  };

  // Function to swap departure and arrival airports
  const swapAirports = () => {
    const temp = departure;
    setDeparture(arrival);
    setArrival(temp);
  };

  return (
    <Card className="search-form">
      <div className="flight-search-element">
        <div className="element-label">Departure Airport: *</div>
        <Select
          {...airportSelectProps}
          value={departure}
          onChange={setDeparture}
          placeholder="Select departure airport..."
          className="airport-select"
          status={errors.departure ? 'error' : ''}
        />
      </div>

      <div className="swap-button-container">
        <Button 
          icon={<SwapOutlined />} 
          onClick={swapAirports}
          type="text"
          className="swap-button"
        />
      </div>

      <div className="flight-search-element">
        <div className="element-label">Arrival Airport: *</div>
        <Select
          {...airportSelectProps}
          value={arrival}
          onChange={setArrival}
          placeholder="Select arrival airport..."
          className="airport-select"
          status={errors.arrival ? 'error' : ''}
        />
      </div>

      <div className="flight-search-element">
        <div className="element-label">Airlines Excluded:</div>
        <Select
          {...airlineSelectProps}
          mode="multiple"
          value={selectedAirlines}
          onChange={setSelectedAirlines}
          options={sortedAirlines}
          placeholder="Select airlines..."
          className="airline-select"
        />
      </div>

      <div className="segments-element">
        <div className="element-label">Maximum Segments *</div>
        <InputNumber
          min={1}
          max={5}
          value={maxSegments}
          onChange={setMaxSegments}
          status={errors.maxSegments ? 'error' : ''}
        />
      </div>

      <Button 
        type="primary"
        icon={<SearchOutlined />}
        onClick={handleSubmit}
        loading={isLoading}
        className="search-button"
      >
        Search
      </Button>

      {/* Search form styles moved to global CSS */}
    </Card>
  );
};

export default SearchForm;
