export default function FlightAvailabilityCalendar({ 
  route, 
  onDateSelect, 
  onClose, 
  availabilityData,
  isLoadingCalendar,
  startDate,
  setStartDate,
  stopoverInfo,
  setStopoverInfo
}) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Select Flight Dates</h2>
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {isLoadingCalendar ? (
          <div className="flex flex-col items-center justify-center py-8">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mb-4"></div>
            <p className="text-gray-600">Searching for available flights...</p>
          </div>
        ) : (
          <>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Start Date
              </label>
              <input
                type="date"
                value={startDate || ''}
                onChange={(e) => setStartDate(e.target.value)}
                className="w-full p-2 border rounded"
                min={dayjs().format('YYYY-MM-DD')}
              />
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Stopover Airport
              </label>
              <select
                value={stopoverInfo?.airport || ''}
                onChange={(e) => {
                  const airport = e.target.value;
                  if (airport) {
                    setStopoverInfo({
                      airport,
                      days: 1
                    });
                  } else {
                    setStopoverInfo(null);
                  }
                }}
                className="w-full p-2 border rounded"
              >
                <option value="">No stopover</option>
                {route.slice(1, -1).map((airport) => (
                  <option key={airport} value={airport}>
                    {airport}
                  </option>
                ))}
              </select>
            </div>

            {stopoverInfo && (
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Stopover Duration (days)
                </label>
                <input
                  type="number"
                  min="1"
                  max="7"
                  value={stopoverInfo.days}
                  onChange={(e) => setStopoverInfo({
                    ...stopoverInfo,
                    days: parseInt(e.target.value)
                  })}
                  className="w-full p-2 border rounded"
                />
              </div>
            )}

            <div className="mb-4">
              <button
                onClick={handleSearch}
                className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
              >
                Search Availability
              </button>
            </div>

            {Object.keys(availabilityData).length > 0 && (
              <div className="grid grid-cols-7 gap-1">
                {/* ... existing calendar code ... */}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}
