import React, { useState } from 'react';
import { Card } from 'antd';
import SearchForm from '../components/FlightSearch/SearchForm';
import ResultsTable from '../components/FlightSearch/ResultsTable';
import FlightDetailsModal from '../components/FlightSearch/FlightDetailsModal';
import useFlightSearch from '../components/FlightSearch/hooks/useFlightSearch';
import dayjs from 'dayjs';

const FlightSearch = () => {
  const {
    searchResults,
    isLoading,
    handleSearch,
    pagination,
    handleTableChange,
    errors,
  } = useFlightSearch();

  const [isModalVisible, setIsModalVisible] = useState(false);
  const [currentRoute, setCurrentRoute] = useState(null);
  const [startDay, setStartDay] = useState(dayjs().format('YYYY-MM-DD'));

  const handleCalendarSearch = (startDate, endDate, stopoverInfo, preserveCalendarData = false) => {
    // Set loading state
    setIsLoading(true);

    // Only clear calendar data if preserveCalendarData is false
    if (!preserveCalendarData) {
      setFlightData({});
    }

    // Proceed with search using the API
    // ... rest of search logic ...

    // After fetching data:
    // If preserveCalendarData is true, merge new data with existing data
    // instead of replacing it completely
    if (preserveCalendarData) {
      setFlightData(prevData => ({
        ...prevData,
        ...newData // Merge with new data
      }));
    } else {
      setFlightData(newData);
    }
  };

  return (
    <div className="flight-search-container">
      <SearchForm 
        onSearch={(params) => {
          setStartDay(params.startDay || dayjs().format('YYYY-MM-DD'));
          handleSearch(params);
        }}
        isLoading={isLoading}
        errors={errors}
      />

      {searchResults && (
        <ResultsTable
          searchResults={searchResults}
          isLoading={isLoading}
          pagination={pagination}
          onTableChange={handleTableChange}
          onRouteSelect={(route) => {
            setCurrentRoute(route);
            setIsModalVisible(true);
          }}
        />
      )}

      <FlightDetailsModal
        isModalOpen={isModalVisible}
        currentRoute={currentRoute}
        onClose={() => {
          setIsModalVisible(false);
          setCurrentRoute(null);
        }}
        startDay={startDay}
      />

      <style jsx>{`
        .flight-search-container {
          max-width: 1200px;
          width: 100%;
          margin: 20px auto;
          padding: 0 12px;
          box-sizing: border-box;
        }
        :global(.ant-table-wrapper) {
          margin: 0;
          width: 100%;
          overflow-x: auto;
        }
        :global(.ant-card) {
          margin-bottom: 20px;
          width: 100%;
        }
        :global(.ant-modal) {
          max-width: 100vw;
          margin: 0;
        }
        :global(.ant-modal-content) {
          max-height: 100vh;
          border-radius: 8px;
        }
        @media (max-width: 768px) {
          .flight-search-container {
            padding: 0 8px;
            margin: 12px auto;
          }
          :global(.ant-modal-content) {
            border-radius: 0;
          }
        }
      `}</style>
    </div>
  );
};

export default FlightSearch;
