import React, { useState, useEffect } from 'react';
import { Modal, DatePicker, Input, Spin, Table, Button, Typography } from 'antd';
import dayjs from 'dayjs';
import { getSegmentColumns } from './segmentColumns';
import useFlightDetails from './hooks/useFlightDetails';
import FlightAvailabilityCalendar from './FlightAvailabilityCalendar';
const { RangePicker } = DatePicker;

const FlightDetailsModal = ({ isVisible, currentRoute, onClose }) => {
  const [dateRangeError, setDateRangeError] = useState(false);
  const {
    selectedDates,
    setSelectedDates,
    apiKey,
    setApiKey,
    segmentDetails,
    isLoadingSegments,
    handleDateSearch,
    handleCalendarSearch,
    resetDetails,
    columns,
    selectedFlights,
    availabilityData,
    isLoadingAvailability,
  } = useFlightDetails(getSegmentColumns);

  // Clear data when modal closes
  useEffect(() => {
    if (!isVisible) {
      resetDetails();
      setDateRangeError(false);
      setSelectedDates(null);
      setApiKey('');
    }
  }, [isVisible]);

  const handleOk = () => {
    handleDateSearch(currentRoute);
  };

  const handleCancel = () => {
    onClose();
  };

  const handleCalendarDateSelect = (dateRange) => {
    setSelectedDates(dateRange);
    setDateRangeError(false);
  };

  const handleCalendarSearchClick = () => {
    if (!selectedDates) {
      setDateRangeError(true);
      return;
    }
    handleDateSearch(currentRoute);
  };

  // Function to group flights by segment with safety checks
  const getSegmentTables = () => {
    if (!segmentDetails?.length || !currentRoute?.length) return [];

    // Group flights by segment index
    const segmentGroups = segmentDetails.reduce((acc, flight) => {
      const segmentIndex = flight.segmentIndex;
      if (!acc[segmentIndex]) {
        acc[segmentIndex] = [];
      }
      acc[segmentIndex].push(flight);
      return acc;
    }, {});

    // Convert to array format with segment info
    return Object.entries(segmentGroups).map(([index, flights]) => {
      const currentIndex = parseInt(index);
      const nextIndex = currentIndex + 1;
      
      // Safety check for route indices
      if (nextIndex >= currentRoute.length) return null;
      
      const segmentRoute = `${currentRoute[currentIndex]}-${currentRoute[nextIndex]}`;
      return {
        index: currentIndex,
        route: segmentRoute,
        flights
      };
    }).filter(Boolean); // Remove any null entries
  };

  // Add pagination settings
  const paginationConfig = {
    pageSize: 5,
    showSizeChanger: true,
    showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} flights`,
    pageSizeOptions: ['5', '10', '20', '50'],
    position: ['bottomLeft']
  };

  return (
    <Modal
      title="Flight Details"
      open={isVisible}
      onCancel={handleCancel}
      footer={null}
      width={1600}
      styles={{
        body: { 
          padding: '12px',
          maxHeight: '90vh',
          overflow: 'auto'
        },
        content: {
          maxWidth: '100vw'
        }
      }}
    >
      <div style={{ marginBottom: 16 }}>
        <div style={{ display: 'flex', gap: '8px', marginBottom: 16 }}>
          <div style={{ display: 'flex', gap: '8px', flex: 1 }}>
            <Input
              placeholder="Enter your yapping password (Under Development)"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              style={{ flex: 1 }}
            />
            <Button
              type="primary"
              disabled={!apiKey || !apiKey.toLowerCase().startsWith('pro')}
              onClick={() => handleCalendarSearch(currentRoute)}
            >
              Apply
            </Button>
          </div>
        </div>
        {dateRangeError && (
          <div style={{ color: 'red' }}>
            Please select a date range in the calendar
          </div>
        )}
      </div>

      <FlightAvailabilityCalendar 
        flightData={availabilityData}
        currentRoute={currentRoute}
        onDateRangeSelect={handleCalendarDateSelect}
        selectedRange={selectedDates}
        onSearch={handleCalendarSearchClick}
      />

      {isLoadingSegments ? (
        <div style={{ textAlign: 'center', margin: '20px 0' }}>
          <Spin />
        </div>
      ) : (
        segmentDetails?.length > 0 && (
          <div style={{ marginTop: 0 }}>
            <Typography.Title level={4} style={{ marginBottom: 0 }}>
              Flights By Segment
            </Typography.Title>
            {getSegmentTables().map((segment, index) => (
              <div key={segment.index} style={{ marginBottom: 0 }}>
                <Typography.Title level={5} style={{ marginBottom: 12 }}>
                  Segment {segment.index} ({segment.route}):
                </Typography.Title>
                <Table
                  columns={columns}
                  dataSource={segment.flights}
                  pagination={paginationConfig}
                  size="small"
                />
                
                {/* Add layover duration if there's a next segment and flights are selected */}
                {index < getSegmentTables().length - 1 && segment.flights.some(f => f.isSelected) && (
                  <div style={{ 
                    padding: '0px',
                    margin: '0px',
                    textAlign: 'center'
                  }}>
                    <Typography.Text strong>
                      {segment.flights.find(f => f.isSelected)?.layoverDuration 
                        ? `Layover duration: ${segment.flights.find(f => f.isSelected).layoverDuration}`
                        : 'Select flights to see layover duration'}
                    </Typography.Text>
                  </div>
                )}
              </div>
            ))}
          </div>
        )
      )}

      {/* Add Summary Table */}
      {Object.keys(selectedFlights).length > 0 && (
        <div style={{ marginTop: 24 }}>
          <Typography.Title level={4}>Journey Summary</Typography.Title>
          <Table
            columns={[
              {
                title: 'From',
                dataIndex: 'from',
                key: 'from',
              },
              {
                title: 'To',
                dataIndex: 'to',
                key: 'to',
              },
              {
                title: 'Airlines',
                dataIndex: 'airlines',
                key: 'airlines',
                render: (airlines) => (
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                    {airlines.split(', ').map(airline => {
                      const airlineCode = airline.split(' ')[0];
                      return (
                        <img 
                          key={airlineCode}
                          src={`${process.env.PUBLIC_URL}/${airlineCode}.png`}
                          alt={airlineCode}
                          style={{ 
                            width: '24px', 
                            height: '24px',
                            objectFit: 'contain',
                            borderRadius: '4px'
                          }} 
                          onError={(e) => {
                            e.target.style.display = 'none';
                          }}
                        />
                      );
                    })}
                    {airlines}
                  </div>
                ),
              },
              {
                title: 'Duration',
                dataIndex: 'duration',
                key: 'duration',
              },
              {
                title: 'Departs',
                dataIndex: 'departs',
                key: 'departs',
              },
              {
                title: 'Arrives',
                dataIndex: 'arrives',
                key: 'arrives',
              },
              {
                title: 'Economy Price',
                dataIndex: 'economyPrice',
                key: 'economyPrice',
              },
              {
                title: 'Business Price (Max %)',
                dataIndex: 'businessPrice',
                key: 'businessPrice',
              },
              {
                title: 'First Price (Max %)',
                dataIndex: 'firstPrice',
                key: 'firstPrice',
              },
            ]}
            dataSource={[{
              key: 'summary',
              from: selectedFlights[0]?.[0]?.from || '-',
              to: selectedFlights[Object.keys(selectedFlights).length - 1]?.[0]?.to || '-',
              airlines: [...new Set(Object.values(selectedFlights).flatMap(flights => 
                flights.map(f => f.airlines)
              ))].join(', '),
              duration: (() => {
                let totalMinutes = 0;
                
                // Add flight durations (already in minutes)
                Object.values(selectedFlights).forEach(flights => {
                  flights.forEach(flight => {
                    totalMinutes += parseInt(flight.duration);
                  });
                });
                
                // Add layover durations
                Object.values(selectedFlights).forEach((flights, index) => {
                  const flight = flights[0];
                  if (flight?.layoverDuration) {
                    const matches = flight.layoverDuration.match(/(\d+)h\s+(\d+)m/);
                    if (matches) {
                      const [_, hours, minutes] = matches;
                      totalMinutes += (parseInt(hours) * 60) + parseInt(minutes);
                    }
                  }
                });
                
                const hours = Math.floor(totalMinutes / 60);
                const minutes = totalMinutes % 60;
                return `${hours}h ${minutes}m`;
              })(),
              departs: selectedFlights[0]?.[0]?.departs || '-',
              arrives: selectedFlights[Object.keys(selectedFlights).length - 1]?.[0]?.arrives || '-',
              economyPrice: '-',
              businessPrice: '-',
              firstPrice: '-',
            }]}
            pagination={false}
            size="small"
          />
        </div>
      )}

      <style jsx>{`
        :global(.ant-table) {
          font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New', monospace;
        }
      `}</style>
    </Modal>
  );
};

export default FlightDetailsModal;
