import React, { useState } from 'react';
import { Select, InputNumber, Button, Card } from 'antd';
import { SearchOutlined, SwapOutlined } from '@ant-design/icons';
import { airports } from '../../data/airports';
import airlines from '../../data/airlines';

const SearchForm = ({ onSearch, isLoading, errors }) => {
  const [departure, setDeparture] = useState(null);
  const [arrival, setArrival] = useState(null);
  const [selectedAirlines, setSelectedAirlines] = useState([]);
  const [maxSegments, setMaxSegments] = useState(4);

  // Sort airlines alphabetically
  const sortedAirlines = [...airlines]
    .map(airline => ({
      ...airline,
      searchStr: `${airline.value} ${airline.label}`.toLowerCase()
    }))
    .sort((a, b) => a.label.localeCompare(b.label));

  const airportSelectProps = {
    showSearch: true,
    allowClear: true,
    suffixIcon: null,
    options: airports.map(airport => ({
      value: airport.IATA,
      label: `${airport.IATA} - ${airport.Name} (${airport.Country})`,
      iata: airport.IATA,
      name: airport.Name
    })),
    optionRender: (option) => {
      // Extract the parts of the label
      const iataCode = option.value;
      const label = option.label;
      const restOfLabel = label.substring(iataCode.length);

      return (
        <span>
          <span style={{ fontWeight: 'bold' }}>{iataCode}</span>
          {restOfLabel}
        </span>
      );
    },
    filterOption: (input, option) => {
      input = input.toString().toLowerCase();
      const iata = option.value.toLowerCase();
      const label = option.label.toLowerCase();

      return iata.includes(input) || label.includes(input);
    },
    filterSort: (optionA, optionB, inputValue) => {
      const input = inputValue.toLowerCase();

      // Get IATA codes
      const iataA = optionA.value.toLowerCase();
      const iataB = optionB.value.toLowerCase();

      // Check if IATA codes start with input
      const aStartsWithInput = iataA.startsWith(input);
      const bStartsWithInput = iataB.startsWith(input);

      // If one starts with input and the other doesn't, prioritize the one that does
      if (aStartsWithInput && !bStartsWithInput) return -1;
      if (!aStartsWithInput && bStartsWithInput) return 1;

      // If both or neither start with input, check if they contain input
      const aContainsInput = iataA.includes(input);
      const bContainsInput = iataB.includes(input);

      // If one contains input in IATA and the other doesn't, prioritize the one that does
      if (aContainsInput && !bContainsInput) return -1;
      if (!aContainsInput && bContainsInput) return 1;

      // If they're still tied, sort alphabetically by IATA
      return iataA.localeCompare(iataB);
    },
    listHeight: 256,
    virtual: true,
    dropdownStyle: { maxHeight: 400 }
  };

  const airlineSelectProps = {
    showSearch: true,
    allowClear: true,
    optionRender: (option) => {
      // Bold the airline code part
      const airlineCode = option.value;
      const airlineName = option.label;
      return (
        <span>
          <span style={{ fontWeight: 'bold' }}>{airlineCode}</span> - {airlineName}
        </span>
      );
    },
    filterOption: (input, option) => {
      input = input.toString().toLowerCase();
      const code = option.value.toLowerCase();
      const label = option.label.toLowerCase();

      return code.includes(input) || label.includes(input);
    },
    filterSort: (optionA, optionB, inputValue) => {
      const input = inputValue.toLowerCase();

      // Get airline codes
      const codeA = optionA.value.toLowerCase();
      const codeB = optionB.value.toLowerCase();

      // Check if codes start with input
      const aStartsWithInput = codeA.startsWith(input);
      const bStartsWithInput = codeB.startsWith(input);

      // If one starts with input and the other doesn't, prioritize the one that does
      if (aStartsWithInput && !bStartsWithInput) return -1;
      if (!aStartsWithInput && bStartsWithInput) return 1;

      // If both or neither start with input, check if they contain input
      const aContainsInput = codeA.includes(input);
      const bContainsInput = codeB.includes(input);

      // If one contains input in code and the other doesn't, prioritize the one that does
      if (aContainsInput && !bContainsInput) return -1;
      if (!aContainsInput && bContainsInput) return 1;

      // If they're still tied, sort alphabetically by code
      return codeA.localeCompare(codeB);
    },
    listHeight: 400,
    virtual: false,
    menuItemSelectedIcon: null,
    dropdownStyle: { maxHeight: 400 }
  };

  const handleSubmit = () => {
    onSearch({
      departure,
      arrival,
      selectedAirlines,
      maxSegments
    });
  };

  // Function to swap departure and arrival airports
  const swapAirports = () => {
    const temp = departure;
    setDeparture(arrival);
    setArrival(temp);
  };

  return (
    <Card className="search-form">
      <div className="flight-search-element">
        <div className="element-label">Departure Airport: *</div>
        <Select
          {...airportSelectProps}
          value={departure}
          onChange={setDeparture}
          placeholder="Select departure airport..."
          className="airport-select"
          status={errors.departure ? 'error' : ''}
        />
      </div>

      <div className="swap-button-container">
        <Button 
          icon={<SwapOutlined />} 
          onClick={swapAirports}
          type="text"
          className="swap-button"
        />
      </div>

      <div className="flight-search-element">
        <div className="element-label">Arrival Airport: *</div>
        <Select
          {...airportSelectProps}
          value={arrival}
          onChange={setArrival}
          placeholder="Select arrival airport..."
          className="airport-select"
          status={errors.arrival ? 'error' : ''}
        />
      </div>

      <div className="flight-search-element">
        <div className="element-label">Airlines Excluded:</div>
        <Select
          {...airlineSelectProps}
          mode="multiple"
          value={selectedAirlines}
          onChange={setSelectedAirlines}
          options={sortedAirlines}
          placeholder="Select airlines..."
          className="airline-select"
        />
      </div>

      <div className="segments-element">
        <div className="element-label">Maximum Segments *</div>
        <InputNumber
          min={1}
          max={5}
          value={maxSegments}
          onChange={setMaxSegments}
          status={errors.maxSegments ? 'error' : ''}
        />
      </div>

      <Button 
        type="primary"
        icon={<SearchOutlined />}
        onClick={handleSubmit}
        loading={isLoading}
        className="search-button"
      >
        Search
      </Button>

      {/* Search form styles moved to global CSS */}
    </Card>
  );
};

export default SearchForm;
