import { useState } from 'react';
import dayjs from 'dayjs';
import routeDetails from '../../../data/route_details.json';
import airlines from '../../../data/airlines';

export default function useFlightDetails() {
  const [selectedDates, setSelectedDates] = useState(null);
  const [apiKey, setApiKey] = useState('');
  const [segmentDetails, setSegmentDetails] = useState([]);
  const [isLoadingSegments, setIsLoadingSegments] = useState(false);

  const formatTime = (dateStr, baseDate) => {
    const date = dayjs(dateStr);
    const base = dayjs(baseDate);
    
    // If time is before 12:00 and it's a flight arrival, it's likely next day
    const dayDiff = date.hour() < 12 && date.format('HH:mm') < '12:00' ? 1 : 0;
    
    const timeStr = date.format('HH:mm');
    return dayDiff > 0 ? `${timeStr} (+${dayDiff})` : timeStr;
  };

  const getAirlineName = (code) => {
    const airline = airlines.find(a => a.value === code);
    return airline ? airline.label.replace(` (${code})`, '') : code;
  };

  const getSegmentDistance = (from, to) => {
    const route = routeDetails.find(r => 
      r.origin === from && 
      r.destination === to
    );

    if (!route) {
      console.log(`Warning: No route found for ${from}-${to}, using default distance`);
      return 1000;
    }

    console.log(`Distance for ${from}-${to}: ${route.distance.toLocaleString()} miles`);
    return route.distance;
  };

  const processFlightData = (data, timeWindow) => {
    const flights = {};
    const baseDate = selectedDates.format('YYYY-MM-DD');
    
    const rawFlights = data.results?.[0]?.data?.data || [];
    console.log(`  Processing ${rawFlights.length} raw flights`);
    
    rawFlights
      .filter(trip => {
        // Filter out non-direct flights
        if (trip.Stops !== 0) {
          console.log(`  Skipping flight with ${trip.Stops} stops`);
          return false;
        }
        
        // Filter out EK and FZ carriers
        if (trip.Carriers === 'EK' || trip.Carriers === 'FZ') {
          console.log(`  Skipping ${trip.Carriers} flight: ${trip.FlightNumbers}`);
          return false;
        }
        
        if (timeWindow) {
          const departureTime = dayjs(trip.DepartsAt.replace('Z', ''));
          const isValid = departureTime.isAfter(timeWindow.earliestDeparture) && 
                         departureTime.isBefore(timeWindow.latestDeparture);
          
          if (!isValid) {
            console.log(`  Skipping flight outside time window: ${departureTime.format('YYYY-MM-DD HH:mm')}`);
          }
          return isValid;
        }
        
        return true;
      })
      .forEach(trip => {
        const flightNumber = trip.FlightNumbers;
        const departureTime = dayjs(trip.DepartsAt.replace('Z', ''));
        const existingFlight = flights[flightNumber];

        // Process aircraft name
        let aircraftName = trip.Aircraft[0];
        if (aircraftName === '787 All') {
          aircraftName = 'Boeing 787-10';
        }
        
        // Update departure and arrival times to use selected date
        const originalDeparture = dayjs(trip.DepartsAt.replace('Z', ''));
        const originalArrival = dayjs(trip.ArrivesAt.replace('Z', ''));
        
        const arrivalTime = dayjs(`${baseDate} ${originalArrival.format('HH:mm')}`);
        
        // If arrival time is before departure time, it must be next day
        if (arrivalTime.isBefore(departureTime)) {
          arrivalTime = arrivalTime.add(1, 'day');
        }

        if (existingFlight) {
          const existingDeparture = dayjs(existingFlight.DepartsAt);
          if (departureTime.isBefore(existingDeparture)) {
            return;
          }
        }

        const baseDayjs = dayjs(baseDate);
        const departDayDiff = departureTime.diff(baseDayjs, 'day');
        const arrivalDayDiff = arrivalTime.diff(baseDayjs, 'day');

        flights[flightNumber] = {
          from: trip.OriginAirport,
          to: trip.DestinationAirport,
          flightNumber: flightNumber,
          airlines: getAirlineName(trip.Carriers),
          aircraft: aircraftName,
          departs: departDayDiff > 0 ? 
            `${departureTime.format('HH:mm')} (+${departDayDiff})` : 
            departureTime.format('HH:mm'),
          arrives: arrivalDayDiff > 0 ? 
            `${arrivalTime.format('HH:mm')} (+${arrivalDayDiff})` : 
            arrivalTime.format('HH:mm'),
          DepartsAt: departureTime.format('YYYY-MM-DD HH:mm:ss'),
          ArrivesAt: arrivalTime.format('YYYY-MM-DD HH:mm:ss'),
          economy: false,
          business: false,
          first: false
        };
        
        switch(trip.Cabin.toLowerCase()) {
          case 'economy':
            flights[flightNumber].economy = true;
            break;
          case 'business':
            flights[flightNumber].business = true;
            break;
          case 'first':
            flights[flightNumber].first = true;
            break;
        }
      });
    
    return Object.values(flights);
  };

  const handleDateSearch = async (currentRoute) => {
    if (!selectedDates || !currentRoute || !apiKey) return;
    
    setIsLoadingSegments(true);
    const processedSegments = [];
    const baseDate = selectedDates.format('YYYY-MM-DD');
    
    try {
      // Process all segments first
      for (let i = 0; i < currentRoute.length - 1; i++) {
        const from = currentRoute[i];
        const to = currentRoute[i + 1];
        const prevSegment = processedSegments[i - 1];
        
        let timeWindow = null;
        if (prevSegment) {
          if (prevSegment.flights.length === 0) {
            const prevFrom = currentRoute[i - 1];
            const prevTo = currentRoute[i];
            const prevDistance = getSegmentDistance(prevFrom, prevTo);
            const minConnectionHours = Math.ceil(prevDistance / 575);
            
            const baseTime = prevSegment.earliestArrival || dayjs(baseDate);
            const endTime = prevSegment.latestArrival || baseTime;
            
            timeWindow = {
              earliestDeparture: baseTime.add(minConnectionHours, 'hour'),
              latestDeparture: endTime.add(24, 'hour').add(minConnectionHours, 'hour')
            };
          } else {
            timeWindow = {
              earliestDeparture: prevSegment.earliestArrival,
              latestDeparture: prevSegment.latestArrival.add(24, 'hour')
            };
          }
        }

        const route = routeDetails.find(r => 
          r.origin === from && 
          r.destination === to &&
          r.date === selectedDates.format('YYYY-MM-DD')
        );

        if (!route) {
          console.log(`\nNo route found for ${from}-${to} on ${selectedDates.format('YYYY-MM-DD')}`);
          console.log('Available dates for this route:');
          const availableRoutes = routeDetails.filter(r => 
            r.origin === from && 
            r.destination === to
          );
          availableRoutes.forEach(r => console.log(`  ${r.date}: ID ${r.ID}`));
          processedSegments.push({ flights: [], earliestArrival: null, latestArrival: null });
          continue;
        }

        console.log(`\nProcessing route ${from}-${to}:`);
        console.log(`Date: ${selectedDates.format('YYYY-MM-DD')}`);
        console.log(`Segment ID: ${route.ID}`);
        
        try {
          const response = await fetch(`https://backend-284998006367.us-central1.run.app/api/seats/${route.ID}`, {
            method: 'GET',
            headers: {
              'accept': 'application/json',
              'Partner-Authorization': apiKey,
              'Segment-ID': route.ID
            }
          });

          if (!response.ok) {
            throw new Error(`Failed to fetch flight details for ${from}-${to}`);
          }

          const data = await response.json();
          const flights = processFlightData(data, timeWindow);

          // Add summary log
          console.log('\nSegment Summary:');
          console.log('================');
          console.log(`From: ${from}`);
          console.log(`To: ${to}`);
          console.log(`Segment ID: ${route.ID}`);
          console.log(`Total Flights: ${flights.length}`);
          console.log('\nFlights:');
          flights.forEach(flight => {
            console.log(`  ${flight.flightNumber}: Departs ${flight.departs}, Arrives ${flight.arrives}`);
          });
          console.log('================\n');

          let earliestArrival = null;
          let latestArrival = null;

          if (flights.length > 0) {
            earliestArrival = dayjs(flights[0].ArrivesAt);
            latestArrival = dayjs(flights[flights.length - 1].ArrivesAt);
          }

          processedSegments.push({
            route: `${from}-${to}`,
            flights,
            earliestArrival,
            latestArrival
          });

        } catch (error) {
          console.error(`Error processing segment ${from}-${to}:`, error);
          processedSegments.push({ flights: [], earliestArrival: null, latestArrival: null });
        }
      }

      // Temporarily disabled connection validation
      // if (processedSegments.length >= 2 && processedSegments[1].flights.length > 0) {
      //   ... disabled code ...
      // }

      setSegmentDetails(processedSegments.flatMap(segment => segment.flights));
    } catch (error) {
      console.error('Error fetching segment details:', error);
    } finally {
      setIsLoadingSegments(false);
    }
  };

  const resetDetails = () => {
    setSelectedDates(null);
    setApiKey('');
    setSegmentDetails([]);
  };

  return {
    selectedDates,
    setSelectedDates,
    apiKey,
    setApiKey,
    segmentDetails,
    isLoadingSegments,
    handleDateSearch,
    resetDetails
  };
}
