import React, { useState, useEffect } from 'react';
import { Modal, DatePicker, Input, Spin, Table, Button } from 'antd';
import dayjs from 'dayjs';
import { getSegmentColumns } from './segmentColumns';
import useFlightDetails from './hooks/useFlightDetails';
import FlightAvailabilityCalendar from './FlightAvailabilityCalendar';
const { RangePicker } = DatePicker;

const FlightDetailsModal = ({ isVisible, currentRoute, onClose }) => {
  const [dateRangeError, setDateRangeError] = useState(false);
  const {
    selectedDates,
    setSelectedDates,
    apiKey,
    setApiKey,
    segmentDetails,
    isLoadingSegments,
    handleDateSearch,
    handleCalendarSearch,
    resetDetails,
    columns,
    selectedFlights,
    availabilityData,
    isLoadingAvailability,
  } = useFlightDetails(getSegmentColumns);

  // Clear data when modal closes
  useEffect(() => {
    if (!isVisible) {
      resetDetails();
      setDateRangeError(false);
      setSelectedDates(null);
      setApiKey('');
    }
  }, [isVisible]);

  const handleOk = () => {
    handleDateSearch(currentRoute);
  };

  const handleCancel = () => {
    onClose();
  };

  const handleCalendarDateSelect = (dateRange) => {
    setSelectedDates(dateRange);
    setDateRangeError(false);
  };

  const handleCalendarSearchClick = () => {
    if (!selectedDates) {
      setDateRangeError(true);
      return;
    }
    handleDateSearch(currentRoute);
  };

  return (
    <Modal
      title="Flight Details"
      open={isVisible}
      onOk={() => handleDateSearch(currentRoute)}
      onCancel={handleCancel}
      width={1200}
    >
      <div style={{ marginBottom: 16 }}>
        <div style={{ display: 'flex', gap: '8px', marginBottom: 16 }}>
          <div style={{ display: 'flex', gap: '8px', flex: 1 }}>
            <Input
              placeholder="Enter API Key"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              style={{ flex: 1 }}
            />
            <Button
              type="primary"
              disabled={!apiKey}
              onClick={() => handleCalendarSearch(currentRoute)}
            >
              Apply
            </Button>
          </div>
        </div>
        {dateRangeError && (
          <div style={{ color: 'red' }}>
            Please select a date range in the calendar
          </div>
        )}
      </div>

      <FlightAvailabilityCalendar 
        flightData={availabilityData}
        currentRoute={currentRoute}
        onDateRangeSelect={handleCalendarDateSelect}
        selectedRange={selectedDates}
        onSearch={handleCalendarSearchClick}
      />

      {isLoadingSegments ? (
        <div style={{ textAlign: 'center', margin: '20px 0' }}>
          <Spin />
        </div>
      ) : (
        segmentDetails.length > 0 && (
          <Table
            columns={columns}
            dataSource={segmentDetails}
            pagination={false}
            size="small"
          />
        )
      )}

      <style jsx>{`
        :global(.ant-table) {
          font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New', monospace;
        }
      `}</style>
    </Modal>
  );
};

export default FlightDetailsModal;
