import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { Layout, Tabs, Button, Breadcrumb } from 'antd';
import { HomeOutlined, SearchOutlined, PushpinOutlined } from '@ant-design/icons';
import './App.css';
import FlightSearch from './pages/FlightSearch';
import NormalRouteBuilder from './pages/NormalRouteBuilder';

const { Header, Content, Footer, Sider } = Layout;

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

// Main app content separated to have access to router context
function AppContent() {
  const currentPath = window.location.pathname;
  const isFlightSearch = currentPath.includes('/ac');
  const isNormalRoute = currentPath.includes('/normal');

  // Create a dynamic breadcrumb based on the current path
  const getBreadcrumbItems = () => {
    if (isFlightSearch) {
      return [
        { title: <Link to="/"><HomeOutlined /> Home</Link> },
        { title: 'AC Route Builder' }
      ];
    } else if (isNormalRoute) {
      return [
        { title: <Link to="/"><HomeOutlined /> Home</Link> },
        { title: 'Normal Route Builder' }
      ];
    } else {
      return [
        { title: <HomeOutlined /> },
        { title: 'Home' }
      ];
    }
  };

  return (
    <Layout className="app-layout">
      <Header className="app-header">
        <div className="logo-container">
          <div className="logo">ROUTE BUILDER</div>
        </div>
        <div className="nav-buttons">
          <Link to="/ac">
            <Button 
              type={isFlightSearch ? "primary" : "default"}
              icon={<SearchOutlined />}
              size="large"
              className="nav-button"
            >
              AC Route Builder
            </Button>
          </Link>
          <Link to="/normal">
            <Button 
              type={isNormalRoute ? "primary" : "default"}
              icon={<PushpinOutlined />}
              size="large"
              className="nav-button"
            >
              Normal Route Builder
            </Button>
          </Link>
        </div>
      </Header>

      <Content className="app-content">
        <div className="content-container">
          <Breadcrumb 
            items={getBreadcrumbItems()}
            className="app-breadcrumb"
          />

          <div className="main-content">
            <Routes>
              <Route path="/ac" element={<FlightSearch />} />
              <Route path="/normal" element={<NormalRouteBuilder />} />
              <Route path="/" element={<Navigate to="/ac" replace />} />
            </Routes>
          </div>
        </div>
      </Content>

      <Footer className="app-footer">
        Route Builder ©{new Date().getFullYear()}
      </Footer>
    </Layout>
  );
}

export default App;
