                      const currentFlight = selectedFlights[i]?.[0];
                      const nextFlight = selectedFlights[i + 1]?.[0];

                      if (currentFlight && nextFlight) {
                        const arrivalTime = dayjs(currentFlight.ArrivesAt);
                        const departureTime = dayjs(nextFlight.DepartsAt);
                        const layoverMinutes = departureTime.diff(arrivalTime, 'minute');

                        console.log(`Checking layover between segments ${i} and ${i+1}:`);
                        console.log('Arrival:', arrivalTime.format('YYYY-MM-DD HH:mm'));
                        console.log('Departure:', departureTime.format('YYYY-MM-DD HH:mm'));
                        console.log('Layover minutes:', layoverMinutes);

                        // If layover is more than 24 hours, this is a stopover point
                        if (layoverMinutes >= 24 * 60) {
                          console.log(`Found stopover at segment ${i}`);
                          stopoverIndex = i;
                          break;
                        }
                      }
                    }

                    console.log('Stopover Index:', stopoverIndex);

                    // If no stopover found, return single row
                    if (stopoverIndex === null) {
