import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useLocation } from 'react-router-dom';
import { Menu, Layout } from 'antd';
import './App.css';
import FlightSearch from './pages/FlightSearch';
import NormalRouteBuilder from './pages/NormalRouteBuilder';

const { Header, Content, Footer } = Layout;

// Navigation menu component with active state tracking
const NavigationMenu = () => {
  const location = useLocation();
  const [selectedKey, setSelectedKey] = useState('ac');

  // Update selected key when location changes
  useEffect(() => {
    const path = location.pathname;
    if (path.includes('/normal')) {
      setSelectedKey('normal');
    } else if (path.includes('/ac') || path === '/') {
      setSelectedKey('ac');
    }
  }, [location]);

  return (
    <Menu
      theme="dark"
      mode="horizontal"
      selectedKeys={[selectedKey]}
      items={[
        {
          key: 'ac',
          label: <Link to="/ac">AC Route Builder</Link>,
        },
        {
          key: 'normal',
          label: <Link to="/normal">Normal Route Builder</Link>,
        },
      ]}
    />
  );
};

function App() {
  return (
    <Router>
      <Layout className="layout">
        <Header style={{ position: 'sticky', top: 0, zIndex: 1, width: '100%', background: '#001529' }}>
          <div className="logo">ROUTES</div>
          <NavigationMenu />
        </Header>

        <Content style={{ padding: '0 50px', minHeight: 'calc(100vh - 134px)' }}>
          <Routes>
            <Route path="/ac" element={<FlightSearch />} />
            <Route path="/normal" element={<NormalRouteBuilder />} />
            <Route path="/" element={<Navigate to="/ac" replace />} />
          </Routes>
        </Content>

        <Footer style={{ textAlign: 'center' }}>
          Route Builder Â©{new Date().getFullYear()}
        </Footer>
      </Layout>
    </Router>
  );
}

export default App;
