import { useState } from 'react';
import dayjs from 'dayjs';
import routeDetails from '../../../data/route_details.json';
import airlines from '../../../data/airlines';
import { getSegmentColumns } from '../segmentColumns';

export default function useFlightDetails(getColumns) {
  const [selectedDates, setSelectedDates] = useState(null);
  const [apiKey, setApiKey] = useState('');
  const [segmentDetails, setSegmentDetails] = useState([]);
  const [isLoadingSegments, setIsLoadingSegments] = useState(false);
  const [selectedFlights, setSelectedFlights] = useState({});
  const [validCombinations, setValidCombinations] = useState([]);
  const [processedSegments, setProcessedSegments] = useState([]);

  const formatTime = (dateStr, baseDate) => {
    const date = dayjs(dateStr);
    const base = dayjs(baseDate);
    
    // If time is before 12:00 and it's a flight arrival, it's likely next day
    const dayDiff = date.hour() < 12 && date.format('HH:mm') < '12:00' ? 1 : 0;
    
    const timeStr = date.format('HH:mm');
    return dayDiff > 0 ? `${timeStr} (+${dayDiff})` : timeStr;
  };

  const getAirlineName = (code) => {
    const airline = airlines.find(a => a.value === code);
    return airline ? airline.label.replace(` (${code})`, '') : code;
  };

  const getSegmentDistance = (from, to) => {
    const route = routeDetails.find(r => 
      r.origin === from && 
      r.destination === to
    );

    if (!route) {
      console.log(`Warning: No route found for ${from}-${to}, using default distance`);
      return 1000;
    }

    console.log(`Distance for ${from}-${to}: ${route.distance.toLocaleString()} miles`);
    return route.distance;
  };

  const processFlightData = (data, timeWindow, segmentIndex) => {
    const flights = {};
    const baseDate = selectedDates.format('YYYY-MM-DD');
    
    const rawFlights = data.results?.[0]?.data?.data || [];
    console.log(`\nProcessing ${rawFlights.length} raw flights:`);
    
    if (timeWindow) {
      console.log('Time Window:', {
        start: timeWindow.start.format('YYYY-MM-DD HH:mm'),
        end: timeWindow.end.format('YYYY-MM-DD HH:mm')
      });
    }
    
    rawFlights
      .filter(trip => {
        console.log(`\nChecking flight ${trip.FlightNumbers}:`);
        console.log(`  Carrier: ${trip.Carriers}`);
        console.log(`  Departs: ${dayjs(trip.DepartsAt).format('YYYY-MM-DD HH:mm')}`);
        console.log(`  Arrives: ${dayjs(trip.ArrivesAt).format('YYYY-MM-DD HH:mm')}`);
        
        // Filter out non-direct flights
        if (trip.Stops !== 0) {
          console.log('  ❌ Skipped: Not a direct flight');
          return false;
        }
        
        // Filter out EK and FZ carriers
        if (trip.Carriers === 'EK' || trip.Carriers === 'FZ') {
          console.log('  ❌ Skipped: Excluded carrier');
          return false;
        }
        
        if (timeWindow) {
          const departureTime = dayjs(trip.DepartsAt);
          const isValid = departureTime.isAfter(timeWindow.start) && 
                         departureTime.isBefore(timeWindow.end);
          
          if (!isValid) {
            console.log('  ❌ Skipped: Outside time window');
            console.log(`    Must depart between ${timeWindow.start.format('YYYY-MM-DD HH:mm')} and ${timeWindow.end.format('YYYY-MM-DD HH:mm')}`);
          } else {
            console.log('  ✓ Accepted: Within time window');
          }
          return isValid;
        }
        
        console.log('  ✓ Accepted: No time window restrictions');
        return true;
      })
      .forEach(trip => {
        const flightNumber = trip.FlightNumbers;
        const departureTime = dayjs(trip.DepartsAt.replace('Z', ''));
        const existingFlight = flights[flightNumber];

        // If this is a duplicate flight, only keep the later one
        if (existingFlight) {
          const existingDeparture = dayjs(existingFlight.DepartsAt.replace('Z', '')); // Remove Z to treat as local time
          if (departureTime.isBefore(existingDeparture)) {
            return; // Skip this one, keep the existing later flight
          }
        }

        const arrivalTime = dayjs(trip.ArrivesAt.replace('Z', '')); // Remove Z to treat as local time
        const baseDayjs = dayjs(baseDate);
        const departDayDiff = departureTime.diff(baseDayjs, 'day');
        const arrivalDayDiff = arrivalTime.diff(baseDayjs, 'day');

        // Process aircraft name
        let aircraftName = trip.Aircraft[0];
        if (aircraftName && aircraftName === '787  All') {
          aircraftName = 'Boeing 787-10';
        }

        flights[flightNumber] = {
          from: trip.OriginAirport,
          to: trip.DestinationAirport,
          flightNumber: flightNumber,
          airlines: getAirlineName(trip.Carriers),
          aircraft: aircraftName,
          duration: trip.TotalDuration,
          departs: departDayDiff > 0 ? 
            `${departureTime.format('HH:mm')} (+${departDayDiff})` : 
            departureTime.format('HH:mm'),
          arrives: arrivalDayDiff > 0 ? 
            `${arrivalTime.format('HH:mm')} (+${arrivalDayDiff})` : 
            arrivalTime.format('HH:mm'),
          DepartsAt: departureTime.format('YYYY-MM-DD HH:mm:ss'),
          ArrivesAt: arrivalTime.format('YYYY-MM-DD HH:mm:ss'),
          economy: false,
          business: false,
          first: false,
          isSelected: false,
          segmentIndex: segmentIndex
        };
        
        switch(trip.Cabin.toLowerCase()) {
          case 'economy':
            flights[flightNumber].economy = true;
            break;
          case 'business':
            flights[flightNumber].business = true;
            break;
          case 'first':
            flights[flightNumber].first = true;
            break;
        }
      });
    
    return Object.values(flights);
  };

  const handleDateSearch = async (currentRoute) => {
    if (!selectedDates || !currentRoute || !apiKey) return;
    
    setIsLoadingSegments(true);
    const newProcessedSegments = [];
    const baseDate = selectedDates.format('YYYY-MM-DD');
    
    try {
      // Process all segments
      for (let i = 0; i < currentRoute.length - 1; i++) {
        const from = currentRoute[i];
        const to = currentRoute[i + 1];
        const prevSegment = processedSegments[i - 1];
        
        let timeWindow = null;
        if (prevSegment) {
          if (prevSegment.flights.length === 0) {
            if (i === 1) {
              timeWindow = {
                start: dayjs(baseDate).startOf('day'),
                end: dayjs(baseDate).add(1, 'day').endOf('day')
              };
            } else {
              break;
            }
          } else {
            // Calculate time window from previous segment's flights
            const arrivals = prevSegment.flights.map(f => {
              // Always base times on the selected date for all segments
              const arrivalTime = dayjs(f.ArrivesAt);
              return dayjs(`${baseDate} ${arrivalTime.format('HH:mm')}`).add(arrivalTime.diff(dayjs(f.ArrivesAt).startOf('day'), 'day'), 'day');
            });
            
            arrivals.sort((a, b) => a.valueOf() - b.valueOf());
            
            timeWindow = {
              start: arrivals[0],
              end: arrivals[arrivals.length - 1].add(24, 'hours')
            };
          }
          
          console.log('\nTime Window for', `${from}-${to}:`);
          console.log('From:', timeWindow.start.format('YYYY-MM-DD HH:mm'));
          console.log('To:', timeWindow.end.format('YYYY-MM-DD HH:mm'));
        }

        // Get all required dates for this segment
        const dates = new Set();
        if (timeWindow) {
          let currentDate = timeWindow.start;
          while (currentDate.isBefore(timeWindow.end) || currentDate.isSame(timeWindow.end, 'day')) {
            dates.add(currentDate.format('YYYY-MM-DD'));
            currentDate = currentDate.add(1, 'day');
          }
        } else {
          dates.add(baseDate);
        }

        console.log(`\nFetching dates for ${from}-${to}:`, Array.from(dates));
        
        // Process each date
        const allFlights = [];
        for (const date of dates) {
          const route = routeDetails.find(r => 
            r.origin === from && 
            r.destination === to &&
            r.date === date
          );

          if (!route) {
            console.log(`No route found for ${from}-${to} on ${date}`);
            continue;
          }

          console.log(`Processing route ${from}-${to} for ${date}:`);
          console.log(`Segment ID: ${route.ID}`);
          
          try {
            const response = await fetch(`https://backend-284998006367.us-central1.run.app/api/seats/${route.ID}`, {
              method: 'GET',
              headers: {
                'accept': 'application/json',
                'Partner-Authorization': apiKey,
                'Segment-ID': route.ID
              }
            });

            if (!response.ok) {
              throw new Error(`Failed to fetch flight details for ${from}-${to}`);
            }

            const data = await response.json();
            const processedFlights = processFlightData(data, timeWindow, i);
            allFlights.push(...processedFlights);
          } catch (error) {
            console.error(`Error processing segment ${from}-${to} for ${date}:`, error);
          }
        }

        // If no flights found for this segment and it's not the first one
        if (allFlights.length === 0 && i > 0) {
          console.log(`\nNo flights found for segment ${from}-${to}`);
          console.log('Skipping remaining segments');
          break;
        }

        const earliestArrival = allFlights.length > 0 ? dayjs(allFlights[0].ArrivesAt) : null;
        const latestArrival = allFlights.length > 0 ? dayjs(allFlights[allFlights.length - 1].ArrivesAt) : null;

        newProcessedSegments.push({
          route: `${from}-${to}`,
          flights: allFlights,
          earliestArrival,
          latestArrival
        });
      }

      // After processing all segments
      if (newProcessedSegments.length >= 2) {
        console.log('\n=== Flight Combinations ===');
        
        // Store valid combinations
        const validCombinations = new Set();
        
        // Function to find valid next flights
        const findValidConnections = (currentFlight, nextSegmentFlights) => {
          const arrival = dayjs(currentFlight.ArrivesAt);
          return nextSegmentFlights.filter(nextFlight => {
            const departure = dayjs(nextFlight.DepartsAt);
            return departure.isAfter(arrival) && 
                   departure.isBefore(arrival.add(24, 'hours'));
          });
        };

        // Function to format flight for display
        const formatFlight = (flight) => {
          return `${flight.flightNumber} (${dayjs(flight.DepartsAt).format('MM-DD')})`;
        };

        // Function to get connection time
        const getConnectionTime = (flight1, flight2) => {
          const connectionTime = dayjs(flight2.DepartsAt).diff(dayjs(flight1.ArrivesAt), 'minutes');
          const hours = Math.floor(connectionTime / 60);
          const minutes = connectionTime % 60;
          return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
        };

        // Recursive function to build combinations
        const buildCombinations = (currentPath, segmentIndex) => {
          if (segmentIndex >= newProcessedSegments.length) {
            // Log the complete combination
            const times = [];
            for (let i = 0; i < currentPath.length - 1; i++) {
              times.push(getConnectionTime(currentPath[i], currentPath[i + 1]));
            }
            
            console.log(
              currentPath.map(flight => formatFlight(flight)).join(', ') +
              ' (' + times.join(', ') + ')'
            );

            // Add all flights in this combination to valid set
            currentPath.forEach(flight => {
              const flightKey = `${flight.flightNumber}_${dayjs(flight.DepartsAt).format('YYYY-MM-DD')}`;
              validCombinations.add(flightKey);
            });
            
            return;
          }

          const lastFlight = currentPath[currentPath.length - 1];
          const nextSegmentFlights = newProcessedSegments[segmentIndex].flights;
          const validNextFlights = findValidConnections(lastFlight, nextSegmentFlights);

          validNextFlights.forEach(nextFlight => {
            buildCombinations([...currentPath, nextFlight], segmentIndex + 1);
          });
        };

        // Start building combinations from each first segment flight
        newProcessedSegments[0].flights.forEach(firstFlight => {
          buildCombinations([firstFlight], 1);
        });
        
        console.log('======================');

        // Filter out flights that aren't part of any valid combination
        const filteredFlights = newProcessedSegments.flatMap((segment, segmentIndex) => 
          segment.flights.filter(flight => {
            const flightKey = `${flight.flightNumber}_${dayjs(flight.DepartsAt).format('YYYY-MM-DD')}`;
            return validCombinations.has(flightKey);
          }).map(flight => ({
            ...flight,
            isSelected: false,
            segmentIndex
          }))
        );

        setSegmentDetails(filteredFlights);
      } else {
        setSegmentDetails(newProcessedSegments.flatMap((segment, index) => 
          segment.flights.map(flight => ({
            ...flight,
            isSelected: false,
            segmentIndex: index
          }))
        ));
      }
    } catch (error) {
      console.error('Error fetching segment details:', error);
    } finally {
      setIsLoadingSegments(false);
    }
  };

  const resetDetails = () => {
    setSelectedDates(null);
    setApiKey('');
    setSegmentDetails([]);
  };

  const handleFlightSelect = (flight, segmentIndex) => {
    console.log('\n=== FLIGHT SELECTION START ===');
    
    // Check if we're deselecting the current flight
    const isDeselecting = selectedFlights[segmentIndex]?.flightNumber === flight.flightNumber && 
                         dayjs(selectedFlights[segmentIndex]?.DepartsAt).format('YYYY-MM-DD') === 
                         dayjs(flight.DepartsAt).format('YYYY-MM-DD');
    
    console.log('Deselection check:', {
      currentFlight: flight.flightNumber,
      selectedFlight: selectedFlights[segmentIndex]?.flightNumber,
      currentDate: dayjs(flight.DepartsAt).format('YYYY-MM-DD'),
      selectedDate: selectedFlights[segmentIndex] ? dayjs(selectedFlights[segmentIndex].DepartsAt).format('YYYY-MM-DD') : 'none',
      isDeselecting
    });
                         
    if (isDeselecting) {
      console.log('\n=== DESELECTING FLIGHT ===');
      console.log('Flight being deselected:', flight.flightNumber);
      
      // Reset to initial state using processedSegments
      const allFlights = processedSegments.flatMap((segment, idx) => 
        segment.flights.map(f => ({
          ...f,
          isSelected: false,
          segmentIndex: idx
        }))
      );
      
      console.log('Total flights in original data:', allFlights.length);
      console.log('Flights per segment:', processedSegments.map((s, i) => 
        `Segment ${i}: ${s.flights.length} flights`
      ));
      
      setSelectedFlights({});
      setSegmentDetails(allFlights);
      console.log('=== DESELECTION COMPLETE ===\n');
      return;
    }

    // Get all segments from the current data for selection logic
    const allSegments = [
      { flights: segmentDetails.filter(f => f.segmentIndex === 0) },
      { flights: segmentDetails.filter(f => f.segmentIndex === 1) },
      { flights: segmentDetails.filter(f => f.segmentIndex === 2) }
    ].filter(segment => segment.flights.length > 0);

    const flightKey = `${flight.flightNumber}_${dayjs(flight.DepartsAt).format('YYYY-MM-DD')}`;
    console.log('\nSelected:', {
      flight: flightKey,
      segment: segmentIndex,
      departs: dayjs(flight.DepartsAt).format('YYYY-MM-DD HH:mm:ss'),
      arrives: dayjs(flight.ArrivesAt).format('YYYY-MM-DD HH:mm:ss')
    });
    
    // Create new selected flights object
    const newSelected = { ...selectedFlights };
    Object.keys(newSelected).forEach(key => {
      if (Number(key) >= segmentIndex) {
        delete newSelected[key];
      }
    });
    newSelected[segmentIndex] = flight;
    
    // Find valid combinations with the selected flight
    const validCombinations = [];
    
    const findNextValidFlights = (currentFlight, currentSegmentIndex, path = []) => {
      console.log(`\nProcessing segment ${currentSegmentIndex}:`, {
        currentFlight: currentFlight.flightNumber,
        pathSoFar: path.map(f => f.flightNumber).join(' -> '),
        remainingSegments: allSegments.length - currentSegmentIndex - 1
      });
      
      // Save combination if we've reached the end
      if (currentSegmentIndex >= allSegments.length - 1) {
        const fullPath = [currentFlight, ...path];
        validCombinations.push(fullPath);
        console.log('✓ Valid combination found:', 
          fullPath.map(f => `${f.flightNumber} (${dayjs(f.DepartsAt).format('MM-DD HH:mm')})`).join(' -> ')
        );
        return;
      }
      
      const nextSegmentIndex = currentSegmentIndex + 1;
      const lastArrival = dayjs(currentFlight.ArrivesAt);
      
      console.log(`Looking for connections in segment ${nextSegmentIndex}:`, {
        afterFlight: currentFlight.flightNumber,
        arrival: lastArrival.format('MM-DD HH:mm'),
        flightsToCheck: allSegments[nextSegmentIndex].flights.length
      });
      
      // Check each flight in the next segment
      allSegments[nextSegmentIndex].flights.forEach(nextFlight => {
        const departure = dayjs(nextFlight.DepartsAt);
        const connectionTime = departure.diff(lastArrival, 'minutes');
        const isValid = departure.isAfter(lastArrival) && connectionTime <= 1440; // 24 hours in minutes
        
        console.log(`Checking connection ${currentFlight.flightNumber} -> ${nextFlight.flightNumber}:`, {
          departure: departure.format('MM-DD HH:mm'),
          connectionTime: `${Math.floor(connectionTime / 60)}h ${connectionTime % 60}m`,
          isValid
        });
        
        if (isValid) {
          console.log(`✓ Valid connection found: ${currentFlight.flightNumber} -> ${nextFlight.flightNumber}`);
          findNextValidFlights(nextFlight, nextSegmentIndex, [...path, nextFlight]);
        }
      });
    };
    
    console.log('\nStarting combination search...');
    findNextValidFlights(flight, segmentIndex);
    
    console.log('\n=== COMBINATIONS FOUND ===');
    console.log(`Total valid combinations: ${validCombinations.length}`);
    validCombinations.forEach((combo, idx) => {
      console.log(`Combination ${idx + 1}:`, combo.map(f => 
        `${f.flightNumber} (${dayjs(f.DepartsAt).format('MM-DD HH:mm')})`
      ).join(' -> '));
    });
    
    // Build new segment details
    const newSegmentDetails = [];
    console.log('\nBuilding segment details...');
    
    // Add all flights from previous segments
    for (let i = 0; i < allSegments.length; i++) {
      console.log(`Processing segment ${i}...`);
      
      if (i < segmentIndex) {
        console.log(`Adding all flights from previous segment ${i}`);
        allSegments[i].flights.forEach(f => {
          newSegmentDetails.push({
            ...f,
            isSelected: newSelected[i]?.flightNumber === f.flightNumber && 
                       dayjs(newSelected[i]?.DepartsAt).format('YYYY-MM-DD') === 
                       dayjs(f.DepartsAt).format('YYYY-MM-DD'),
            segmentIndex: i
          });
        });
      } else if (i === segmentIndex) {
        console.log(`Adding all flights for current segment ${i}`);
        allSegments[i].flights.forEach(f => {
          newSegmentDetails.push({
            ...f,
            isSelected: f.flightNumber === flight.flightNumber && 
                       dayjs(f.DepartsAt).format('YYYY-MM-DD') === 
                       dayjs(flight.DepartsAt).format('YYYY-MM-DD'),
            segmentIndex: i
          });
        });
      } else {
        console.log(`Filtering valid flights for subsequent segment ${i}`);
        const validFlightsInSegment = new Set(
          validCombinations
            .map(combo => combo[i - segmentIndex])
            .filter(Boolean)
            .map(f => `${f.flightNumber}_${dayjs(f.DepartsAt).format('YYYY-MM-DD')}`)
        );
        
        console.log(`Valid flights in segment ${i}:`, Array.from(validFlightsInSegment));
        
        const validFlights = allSegments[i].flights
          .filter(f => validFlightsInSegment.has(`${f.flightNumber}_${dayjs(f.DepartsAt).format('YYYY-MM-DD')}`));
        
        console.log(`Adding ${validFlights.length} valid flights to segment ${i}`);
        
        validFlights.forEach(f => {
          newSegmentDetails.push({
            ...f,
            isSelected: false,
            segmentIndex: i
          });
        });
      }
    }
    
    setSelectedFlights(newSelected);
    console.log('\n=== FINAL UPDATE ===');
    console.log('Total flights in table:', newSegmentDetails.length);
    console.log('=== END OF PROCESSING ===\n');
    setSegmentDetails(newSegmentDetails);
  };

  // Create columns with the handler
  const columns = getColumns(handleFlightSelect);

  return {
    selectedDates,
    setSelectedDates,
    apiKey,
    setApiKey,
    segmentDetails,
    isLoadingSegments,
    handleDateSearch,
    resetDetails,
    selectedFlights,
    setSelectedFlights,
    validCombinations,
    setValidCombinations,
    handleFlightSelect,
    columns
  };
}
