import React from 'react';
import Select from 'react-select';
import PropTypes from 'prop-types';

/**
 * Flight search form component
 */
const FlightSearchForm = ({ 
  departure, 
  setDeparture, 
  arrival, 
  setArrival, 
  selectedAirlines, 
  setSelectedAirlines, 
  maxSegments, 
  setMaxSegments,
  airportOptions,
  airlineOptions,
  onSearch
}) => {
  /**
   * Handle form submission
   */
  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch();
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Departure Airport */}
        <div>
          <label className="block mb-2 font-medium">Departure Airport</label>
          <Select
            value={departure}
            onChange={setDeparture}
            options={airportOptions}
            placeholder="Select departure airport"
            isClearable
            className="react-select-container"
            classNamePrefix="react-select"
          />
        </div>

        {/* Arrival Airport */}
        <div>
          <label className="block mb-2 font-medium">Arrival Airport</label>
          <Select
            value={arrival}
            onChange={setArrival}
            options={airportOptions}
            placeholder="Select arrival airport"
            isClearable
            className="react-select-container"
            classNamePrefix="react-select"
          />
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Airlines */}
        <div>
          <label className="block mb-2 font-medium">Airlines (Optional)</label>
          <Select
            isMulti
            value={selectedAirlines}
            onChange={setSelectedAirlines}
            options={airlineOptions}
            placeholder="Select airlines"
            className="react-select-container"
            classNamePrefix="react-select"
          />
        </div>

        {/* Max Segments */}
        <div>
          <label className="block mb-2 font-medium">Maximum Segments</label>
          <input
            type="number"
            min={1}
            max={5}
            value={maxSegments}
            onChange={(e) => setMaxSegments(Number(e.target.value))}
            className="w-full p-2 border rounded"
          />
        </div>
      </div>

      {/* Search Button */}
      <div className="mt-6">
        <button 
          type="submit" 
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded transition duration-200"
        >
          Search Flights
        </button>
      </div>
    </form>
  );
};

FlightSearchForm.propTypes = {
  departure: PropTypes.object,
  setDeparture: PropTypes.func.isRequired,
  arrival: PropTypes.object,
  setArrival: PropTypes.func.isRequired,
  selectedAirlines: PropTypes.array.isRequired,
  setSelectedAirlines: PropTypes.func.isRequired,
  maxSegments: PropTypes.number.isRequired,
  setMaxSegments: PropTypes.func.isRequired,
  airportOptions: PropTypes.array.isRequired,
  airlineOptions: PropTypes.array.isRequired,
  onSearch: PropTypes.func.isRequired
};

export default FlightSearchForm;
