import { CheckOutlined } from '@ant-design/icons';

export const getSegmentColumns = () => [
  { 
    title: 'Flight', 
    dataIndex: 'flightNumber',
    width: 100,
    sorter: (a, b) => a.flightNumber.localeCompare(b.flightNumber),
    defaultSortOrder: 'ascend'
  },
  { 
    title: 'Airlines', 
    dataIndex: 'airlines', 
    width: 200,
    sorter: (a, b) => a.airlines.localeCompare(b.airlines)
  },
  { 
    title: 'Aircraft', 
    dataIndex: 'aircraft', 
    width: 100,
    sorter: (a, b) => a.aircraft.localeCompare(b.aircraft)
  },
  { 
    title: 'Departs', 
    dataIndex: 'departs', 
    width: 100,
    sorter: (a, b) => {
      const getTime = (str) => {
        const [time, day] = str.split(' ');
        const dayOffset = day ? parseInt(day.match(/\+(\d+)/)[1]) : 0;
        const [hours, minutes] = time.split(':').map(Number);
        return (dayOffset * 24 * 60) + (hours * 60) + minutes;
      };
      return getTime(a.departs) - getTime(b.departs);
    }
  },
  { 
    title: 'Arrives', 
    dataIndex: 'arrives', 
    width: 100,
    sorter: (a, b) => {
      const getTime = (str) => {
        const [time, day] = str.split(' ');
        const dayOffset = day ? parseInt(day.match(/\+(\d+)/)[1]) : 0;
        const [hours, minutes] = time.split(':').map(Number);
        return (dayOffset * 24 * 60) + (hours * 60) + minutes;
      };
      return getTime(a.arrives) - getTime(b.arrives);
    }
  },
  { 
    title: 'Economy', 
    dataIndex: 'economy', 
    width: 80,
    align: 'center',
    sorter: (a, b) => (a.economy === b.economy ? 0 : a.economy ? -1 : 1),
    render: hasClass => hasClass ? <CheckOutlined style={{ color: '#52c41a' }} /> : null
  },
  { 
    title: 'Business', 
    dataIndex: 'business', 
    width: 80,
    align: 'center',
    sorter: (a, b) => (a.business === b.business ? 0 : a.business ? -1 : 1),
    render: hasClass => hasClass ? <CheckOutlined style={{ color: '#52c41a' }} /> : null
  },
  { 
    title: 'First', 
    dataIndex: 'first', 
    width: 80,
    align: 'center',
    sorter: (a, b) => (a.first === b.first ? 0 : a.first ? -1 : 1),
    render: hasClass => hasClass ? <CheckOutlined style={{ color: '#52c41a' }} /> : null
  }
];
