import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { Menu, Layout } from 'antd';
import './App.css';
import FlightSearch from './pages/FlightSearch';
import NormalRouteBuilder from './pages/NormalRouteBuilder';

const { Header, Content, Footer } = Layout;

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

// Main app content separated to have access to router context
function AppContent() {
  // Get the current path to determine which menu item should be active
  const currentPath = window.location.pathname;
  const selectedKey = currentPath.includes('/normal') ? 'normal' : 'ac';

  return (
    <Layout className="layout">
      <Header style={{ position: 'sticky', top: 0, zIndex: 1, width: '100%', background: '#001529' }}>
        <div className="logo">ROUTES</div>
        <Menu
          theme="dark"
          mode="horizontal"
          selectedKeys={[selectedKey]}
          items={[
            {
              key: 'ac',
              label: <Link to="/ac">AC Route Builder</Link>,
            },
            {
              key: 'normal',
              label: <Link to="/normal">Normal Route Builder</Link>,
            },
          ]}
        />
      </Header>

      <Content style={{ padding: '0 50px', minHeight: 'calc(100vh - 134px)' }}>
        <Routes>
          <Route path="/ac" element={<FlightSearch />} />
          <Route path="/normal" element={<NormalRouteBuilder />} />
          <Route path="/" element={<Navigate to="/ac" replace />} />
        </Routes>
      </Content>

      <Footer style={{ textAlign: 'center' }}>
        Route Builder Â©{new Date().getFullYear()}
      </Footer>
    </Layout>
  );
}

export default App;
